import {useState} from "react";

export const HelloWorld = (prop) => {
    const [message, setMessage] = useState(prop.greet + prop.message);
    return (
        <div className={'HelloWorld'}>
            <h1 onClick={()=>{
                let change = 0;
                change++;
                if (change % 2 === 0) {
                    setMessage(message)
                } else{
                    setMessage('You Clicked My Message!')
                }

            }}>{message}</h1>

        </div>
    )
}
